mapper.select:
       select sum(x.scores) as scores ,sum(tasknum) as tasknum,x.deptid  
     from (
		  select sum(a.score)*b.ratio/100 as scores ,count(1) as tasknum,b.dutyid,b.deptid
		  from tk_task a inner join md_deptduty b on a.deptdutyid=b.dutyid
		  where  a.deptid=#{deptid,jdbcType=INTEGER}
		  	and DATE_FORMAT(a.publishdt,'%Y%m')=DATE_FORMAT(#{publish,jdbcType=TIMESTAMP},'%Y%m') 
		  group by b.dutyid,b.deptid
    ) x
   GROUP BY x.deptid
